package com.jie.utils;

import java.io.File;
import java.io.FileInputStream;
import java.io.FileOutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.OutputStream;
import java.util.Date;
import java.util.HashMap;
import java.util.Map;

import sun.misc.BASE64Decoder;
import sun.misc.BASE64Encoder;

public class Base64Images {
    
    /**
     * 将图片文件转化为字节数组字符串，并对其进行Base64编码处理
     * @param imgFilePath
     * @return
     */
    public static String GetImageStr(String imgFilePath) {
        byte[] data = null;
        
        // 读取图片字节数组
        try {
            InputStream in = new FileInputStream(imgFilePath);
            data = new byte[in.available()];
            in.read(data);
            in.close();
        } catch (IOException e) {
            e.printStackTrace();
        }
        
        // 对字节数组Base64编码
        BASE64Encoder encoder = new BASE64Encoder();
        return encoder.encode(data);// 返回Base64编码过的字节数组字符串
    }
    
    
    /**
     * 对字节数组字符串进行Base64解码并生成图片
     * @param imgStr
     * @param imgFilePath
     * @return
     */
    public static boolean GenerateImage(String imgStr, String imgFilePath) {
    	//图像数据为空
    	if (imgStr == null) return false;
        //分离图片类型和base64图片字符编码
    	Map<String,Object> temp = splitPictureCodeAndType(imgStr);
        BASE64Decoder decoder = new BASE64Decoder();
        //构建文件目录
        if(imgFilePath!=null){ 
	        File fileDir = new File(imgFilePath);
	        if (!fileDir.exists()) fileDir.mkdirs();
	        // 检查目录写权限
	 		if (!fileDir.canWrite()) return false;
        }
        //生成文件名称
        String imgName = DateUtils.formatDate(new Date(), "yyyyMMddhhmmss")
    			+ ((int)(Math.random()*(99999-10000)+10000)) + "." 
        		+ temp.get("type").toString();
        //Base64解码
        try {
            byte[] bytes = decoder.decodeBuffer(temp.get("code").toString());
            for (int i = 0; i < bytes.length; ++i) {
                if (bytes[i] < 0) {// 调整异常数据
                    bytes[i] += 256;
                }
            }
            //生成图片
            OutputStream out = new FileOutputStream(imgFilePath+imgName);
            out.write(bytes);
            out.flush();
            out.close();
            return true;
        } catch (Exception e) {
            return false;
        }
    }
    
    
    /**
     * 功能描述 获取图片后缀名
     * @param fileName
     * @return
     */
    public static String getPictureType(String fileName){
    	return fileName.substring(fileName.lastIndexOf(".") + 1).toLowerCase();
    }
    
    
    /**
     * 功能描述    分离图片类型和base64图片字符编码
     * @param imgBase64Str
     * @return
     */
    public static Map<String,Object> splitPictureCodeAndType(String imgBase64Str) { 
    	Map<String,Object> map = new HashMap<String, Object>();
    	String arr[] = imgBase64Str.split(",");
		map.put("code", arr[1]);
		String str = arr[0].substring(0,arr[0].indexOf(";"));
		String type = str.substring(str.lastIndexOf("/") + 1).toLowerCase();
		map.put("type", type);
		return map;
    }
  
    
    public static void main(String[] args) {
        // 测试从Base64编码转换为图片文件
        String strImg = "data:image/jpg;base64,";
        String saveFilePath = PropertiesUtils.getFileIO("savePicUrl", "properties/config.properties");
		String filePath = GolbalUtils.USER_IMG_PATH;	
		//保存图片
		GenerateImage(strImg, saveFilePath+filePath);
        
        // 测试从图片文件转换为Base64编码
        //System.out.println(GetImageStr("F:/user-img.png"));

    }
    
}
